import React, { useState } from 'react';
import {
  Calendar,
  Users,
  Plus,
  LogIn,
  LogOut,
  User,
  Clock,
  MapPin,
  Search,
} from 'lucide-react';
import './App.css';

export default function ClassBookingApp() {
  const [currentUser, setCurrentUser] = useState(null);
  const [users, setUsers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [view, setView] = useState('login');

const handleLogin = (user) => {
  setCurrentUser(user);
  setView(user.isAdmin ? 'admin-dashboard' : 'client-dashboard');
};


  const handleRegister = (userData) => {
    const newUser = { ...userData, id: Date.now() };
    setUsers([...users, newUser]);
    return true;
  };

  const handleCreateClass = (classData) => {
    const newClass = { ...classData, id: Date.now() };
    setClasses([...classes, newClass]);
  };

  const handleBookClass = (classId) => {
    const newBooking = {
      id: Date.now(),
      classId,
      userId: currentUser.id,
      bookedAt: new Date().toISOString(),
    };
    setBookings([...bookings, newBooking]);
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setView('login');
  };

  return (
    <div className="app-background">
      {view === 'login' && (
        <LoginView
          onLogin={handleLogin}
          onSwitchToRegister={() => setView('register')}
        />
      )}
      {view === 'register' && (
        <RegisterView
          onRegister={handleRegister}
          onSwitchToLogin={() => setView('login')}
        />
      )}
      {view === 'admin-dashboard' && (
        <AdminDashboard
          currentUser={currentUser}
          classes={classes}
          users={users}
          bookings={bookings}
          onCreateClass={handleCreateClass}
          onLogout={handleLogout}
          onViewChange={setView}
        />
      )}
      {view === 'client-dashboard' && (
        <ClientDashboard
          currentUser={currentUser}
          classes={classes}
          bookings={bookings}
          onBookClass={handleBookClass}
          onLogout={handleLogout}
        />
      )}
      {view === 'calendar-view' && (
        <CalendarView
          classes={classes}
          bookings={bookings}
          onBack={() => setView('admin-dashboard')}
        />
      )}
    </div>
  );
}

/* ------------------- LOGIN ------------------- */
function LoginView({ onLogin, onSwitchToRegister }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (!res.ok) throw new Error(data.error || 'Login failed');

      // Pass user to parent
      onLogin(data.user);
      setError('');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit}>
        <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email" required />
        <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required />
        {error && <p className="text-red-500">{error}</p>}
        <button type="submit">Login</button>
      </form>
      <button onClick={onSwitchToRegister}>Register</button>
    </div>
  );
}


/* ------------------- REGISTER ------------------- */
function RegisterView({ onSwitchToLogin }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    phone: '',
    isAdmin: false
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await res.json();

      if (!res.ok) throw new Error(data.error || 'Registration failed');

      setSuccess(true);
      setError('');

      setTimeout(() => {
        onSwitchToLogin();
      }, 1500);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="register-container">
      <form onSubmit={handleSubmit}>
        <input type="text" value={formData.name} onChange={e => setFormData({ ...formData, name: e.target.value })} placeholder="Full Name" required />
        <input type="email" value={formData.email} onChange={e => setFormData({ ...formData, email: e.target.value })} placeholder="Email" required />
        <input type="password" value={formData.password} onChange={e => setFormData({ ...formData, password: e.target.value })} placeholder="Password" required />
        <input type="tel" value={formData.phone} onChange={e => setFormData({ ...formData, phone: e.target.value })} placeholder="Phone" required />
        <label>
          <input type="checkbox" checked={formData.isAdmin} onChange={e => setFormData({ ...formData, isAdmin: e.target.checked })} />
          Register as Admin
        </label>
        {error && <p className="text-red-500">{error}</p>}
        {success && <p className="text-green-500">Account created successfully!</p>}
        <button type="submit">Register</button>
      </form>
      <button onClick={onSwitchToLogin}>Back to Login</button>
    </div>
  );
}


/* ------------------- ADMIN DASHBOARD ------------------- */
function AdminDashboard({
  currentUser,
  classes,
  users,
  bookings,
  onCreateClass,
  onLogout,
  onViewChange,
}) {
  const [newClass, setNewClass] = useState({
    name: '',
    instructor: '',
    date: '',
    time: '',
    location: '',
  });

  const handleCreate = (e) => {
    e.preventDefault();
    onCreateClass(newClass);
    setNewClass({
      name: '',
      instructor: '',
      date: '',
      time: '',
      location: '',
    });
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>
          <Users className="icon-small" /> Welcome, {currentUser.name}
        </h1>
        <div className="dashboard-actions">
          <button className="btn-secondary" onClick={() => onViewChange('calendar-view')}>
            <Calendar className="icon-small" /> View Calendar
          </button>
          <button className="btn-danger" onClick={onLogout}>
            <LogOut className="icon-small" /> Logout
          </button>
        </div>
      </header>

      <section className="dashboard-section">
        <h2>
          <button className="btn-primary" > Create New Class </button>
        </h2>
        <form onSubmit={handleCreate} className="class-form">
          <input
            type="text"
            placeholder="Class Name"
            value={newClass.name}
            onChange={(e) => setNewClass({ ...newClass, name: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Instructor"
            value={newClass.instructor}
            onChange={(e) =>
              setNewClass({ ...newClass, instructor: e.target.value })
            }
            required
          />
          <input
            type="date"
            value={newClass.date}
            onChange={(e) => setNewClass({ ...newClass, date: e.target.value })}
            required
          />
          <input
            type="time"
            value={newClass.time}
            onChange={(e) => setNewClass({ ...newClass, time: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Location"
            value={newClass.location}
            onChange={(e) =>
              setNewClass({ ...newClass, location: e.target.value })
            }
            required
          />
          <input
            type="text"
            placeholder="Description"
            value={newClass.Description}
            onChange={(e) =>
              setNewClass({ ...newClass, Description: e.target.value })
            }
            required
          />
          <button type="submit" className="btn-primary">
            Add Class
          </button>
        </form>
      </section>

      <section className="dashboard-section">
        <h2>Existing Classes</h2>
        <div className="card-grid">
          {classes.map((c) => (
            <div key={c.id} className="card">
              <h3>{c.name}</h3>
              <p>
                <User className="icon-tiny" /> {c.instructor}
              </p>
              <p>
                <Clock className="icon-tiny" /> {c.date} @ {c.time}
              </p>
              <p>
                <MapPin className="icon-tiny" /> {c.location}
              </p>
            </div>
          ))}
          {classes.length === 0 && <p>No classes yet.</p>}
        </div>
      </section>
    </div>
  );
}

/* ------------------- CLIENT DASHBOARD ------------------- */
function ClientDashboard({ currentUser, classes, bookings, onBookClass, onLogout }) {
  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>
          <User className="icon-small" /> Hi, {currentUser.name}
        </h1>
        <button className="btn-danger" onClick={onLogout}>
          <LogOut className="icon-small" /> Logout
        </button>
      </header>

      <section className="dashboard-section">
        <h2>Available Classes</h2>
        <div className="card-grid">
          {classes.map((c) => (
            <div key={c.id} className="card">
              <h3>{c.name}</h3>
              <p>
                <User className="icon-tiny" /> {c.instructor}
              </p>
              <p>
                <Clock className="icon-tiny" /> {c.date} @ {c.time}
              </p>
              <p>
                <MapPin className="icon-tiny" /> {c.location}
              </p>
              <button className="btn-primary" onClick={() => onBookClass(c.id)}>
                Book
              </button>
            </div>
          ))}
          {classes.length === 0 && <p>No available classes yet.</p>}
        </div>
      </section>

      <section className="dashboard-section">
        <h2>Your Bookings</h2>
        <ul className="booking-list">
          {bookings
            .filter((b) => b.userId === currentUser.id)
            .map((b) => {
              const cls = classes.find((c) => c.id === b.classId);
              return (
                <li key={b.id}>
                  {cls ? `${cls.name} - ${cls.date} @ ${cls.time}` : 'Unknown class'}
                </li>
              );
            })}
          {bookings.filter((b) => b.userId === currentUser.id).length === 0 && (
            <p>You have no bookings yet.</p>
          )}
        </ul>
      </section>
    </div>
  );
}

/* ------------------- CALENDAR VIEW ------------------- */
function CalendarView({ classes, bookings, onBack }) {
  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>
          <Calendar className="icon-small" /> Class Calendar
        </h1>
        <button className="btn-secondary" onClick={onBack}>
          Back
        </button>
      </header>

      <section className="dashboard-section">
        <div className="calendar-grid">
          {classes.map((c) => (
            <div key={c.id} className="card calendar-item">
              <h3>{c.name}</h3>
              <p>{c.date} @ {c.time}</p>
              <p>{c.location}</p>
            </div>
          ))}
          {classes.length === 0 && <p>No classes scheduled.</p>}
        </div>
      </section>
    </div>
  );
}
